import React, { useState } from "react";

function App() {
  let [inputitem, setinputitem] = useState("");
  let [saveinput, setsaveinput] = useState([]);
  function handleinput(event) {
    let value = event.target.value;
if(value===""){
    console.log("enter some thing")
}
else{
    setinputitem(value);
}
  }
  function add(event) {
    let empty=event.target.value;
    if(empty===" "){
console.log("Enter some value")

    }
    else{
    setsaveinput((previtem) => {
      return [...previtem, inputitem];
    });

    setinputitem("");
}
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handleinput} type="text" value={inputitem} />
        <button onClick={add}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {saveinput.map((item) => {
            return <li>{item}</li>;
          })}
        </ul>
      </div>
    </div>
  );
}

export default App;
